import React,{ useReducer } from 'react';
import {Context} from '../../App'


const TodoItem = ({id, completed, text}) => {
    const appContext = useContext(Context)

    return( <div style={{ display: 'flex',
                      flexDirection: 'row',
                      justifyContent: 'space-between'  }}>
        <input type="checkbox" checked={completed} onChange={()=>appContext.appDispatch({type: 'completed',payload: id})}/>
        <input type="text" defaultValue={text} />{id}
        <button onClick={()=>appContext.appDispatch({type: 'delete', payload: id})}>Delete</button>
    </div> )
}

export default TodoItem;
