import React,{ useReducer, useContext, useEffect } from 'react';
import appReducer from '../../appReducer';


export default function AboutUs(){
    const [state, dispatch] = useReducer(appReducer, []);

    useEffect(()=>{
        const row = localStorage.getItem('data');
        dispatch({type: 'reset', payload: JSON.parse(row)})
    },[])

    useEffect(() =>{
        localStorage.setItem('data', JSON.stringify(state))
    },[state])

    return (<> 
        <div> Hello this is About-us page</div>
        <br/><br/>        
        <h5>Todo app</h5>
        <button onClick={()=>dispatch({type:'add'})}>New todo</button>
        <br/><br/>
        <TodosList items={state} />
    </>);
}


const TodosList = ({items}) => {

    return(
        items.map(item =>(
            <TodoItem key={item.id} {...item} />
        ))
    )
}

const TodoItem = ({id, completed, text}) => {
    // const dispatch = useContext(Context)
    const [state, dispatch] = useReducer(appReducer, []);
return( <div style={{ display: 'flex',
                      flexDirection: 'row',
                      justifyContent: 'space-between'  }}>
                          {/* Hello */}
        <input type="checkbox" checked={completed} onChange={()=>dispatch({type: 'completed',payload: id})}/>
        <input type="text" defaultValue={text} />{id}
        <button onClick={()=>dispatch({type: 'delete', payload: id})}>Delete</button>
    </div> )
}
